import { motion } from 'framer-motion';
import { useState } from 'react';
import Products from '../products/Products';
import { orders, products } from '../../data.js';


export default function Orders() {
    const [selectedType, setSelectedType] = useState('');
    const [selectedSpecification, setSelectedSpecification] = useState('');

    const types = [...new Set(products.map(p => p.type))];
    const specifications = [...new Set(products.map(p => p.specification))];



    return(
        <motion.div
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -30 }}
      transition={{ duration: 0.3 }}
    >


            <div className="orders-wrapper">
                <h1>Orders</h1>
                <select value={selectedType} onChange={e => setSelectedType(e.target.value)}>
                    <option value="">All types</option>
                    {types.map(type => <option key={type} value={type}>{type}</option>)}
                </select>

                <select value={selectedSpecification} onChange={e => setSelectedSpecification(e.target.value)}>
                    <option value="">All Specifications</option>
                    {specifications.map(spec => <option key={spec} value={spec}>{spec}</option>)}
                </select>

                {orders.map(order => {
                    const filteredProduct = products.filter(p => 
                        p.order === order.id &&
                        (selectedType === '' || p.type === selectedType) &&
                        (selectedSpecification === '' || p.specification === selectedSpecification)
                    );
                    
                     console.log('filteredProduct for order', order.id, filteredProduct);
                    return (
                        <div key={order.id}>
                            <h2>{order.title}</h2>
                            <Products products={filteredProduct} />
                        </div>
                    );
                })}
            </div>
        </motion.div>
    )
}
