import './styles/styles.css';
import { useState, useEffect } from 'react';
import socket from '../../socket';




export default function TopMenu() {
  const [sessionCount, setSessionCount] = useState(0);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleString());
  const [inputValue, setInputValue] = useState('');

  const inputHandleChange = (event) => {
    const newValue = event.target.value;
    setInputValue(newValue);
    console.log('input value', newValue);
  }

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date().toLocaleString());
    }, 1000);
     return () => clearInterval(interval); 
  }, []);

  useEffect(() => {
    socket.on('sessionCount', (count) => {
        setSessionCount(count);
    })
    return () => {
        socket.off('sessionCount');
    };
  }, []);

  return (
    <div className="wrapper-topmenu">
      <input className='topmenu_input' type="text" value={inputValue} onChange={inputHandleChange} placeholder='поиск'/>
      <div className='dataSession-box'>
        <div className='sessionCount'>{sessionCount}</div>
        <div className='data'>{currentTime}</div>
      </div>
    </div>
  );
}
